import React, { useState } from 'react';
import Card from './Card';
import Modal from './Modal';

function List({ list, onCreateCard }) {
    const [showModal, setShowModal] = useState(false);
    const [newCard, setNewCard] = useState('');

    const handleCreate = async () => {
        await onCreateCard(list.id, newCard);
        setNewCard('');
        setShowModal(false);
    }

    return (
        <div className="card me-3" style={{width:'18rem'}}>
            <div className="card-body">
                <h5 className="card-title">{list.name}</h5>
                {list.cards.map(c => <Card key={c.id} card={c} />)}
                <button className="btn btn-sm btn-outline-primary mt-2" onClick={() => setShowModal(true)}>+ Card</button>
            </div>
            {showModal && (
                <Modal title="New Card" onClose={() => setShowModal(false)}>
                    <input className="form-control" placeholder="Card title" value={newCard} onChange={e => setNewCard(e.target.value)} />
                    <button className="btn btn-success mt-2" onClick={handleCreate}>Create</button>
                </Modal>
            )}
        </div>
    );
}

export default List;
