import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import List from '../components/List';
import Modal from '../components/Modal';
import { getLists, createList, createCard } from '../api';

function Board() {
    const { boardId } = useParams();
    const [lists, setLists] = useState([]);
    const [showListModal, setShowListModal] = useState(false);
    const [newList, setNewList] = useState('');

    const fetchLists = async () => {
        const data = await getLists(boardId);
        setLists(data);
    }

    useEffect(() => { fetchLists() }, [boardId]);

    const handleCreateList = async () => {
        await createList(boardId, newList);
        setShowListModal(false);
        setNewList('');
        fetchLists();
    }

    const handleCreateCard = async (listId, title) => {
        await createCard(listId, title);
        fetchLists();
    }

    return (
        <div className="container mt-4">
            <button className="btn btn-primary mb-3" onClick={() => setShowListModal(true)}>New List</button>
            <div className="d-flex">
                {lists.map(l => <List key={l.id} list={l} onCreateCard={handleCreateCard} />)}
            </div>

            {showListModal && (
                <Modal title="New List" onClose={() => setShowListModal(false)}>
                    <input className="form-control" placeholder="List name" value={newList} onChange={e => setNewList(e.target.value)} />
                    <button className="btn btn-success mt-2" onClick={handleCreateList}>Create</button>
                </Modal>
            )}
        </div>
    );
}

export default Board;
