import React, { useEffect, useState } from 'react';
import { getProjects, createProject } from '../api';
import Modal from '../components/Modal';
import { useParams, Link } from 'react-router-dom';

function Dashboard() {
    const [projects, setProjects] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [newProject, setNewProject] = useState('');

    useEffect(() => {
        fetchProjects();
    }, []);

    const fetchProjects = async () => {
        const data = await getProjects();
        setProjects(data);
    }

    const handleCreate = async () => {
        await createProject(newProject);
        setShowModal(false);
        setNewProject('');
        fetchProjects();
    }

    return (
        <div className="container mt-4">
            <h2>Projects</h2>
            <button className="btn btn-primary mb-3" onClick={() => setShowModal(true)}>New Project</button>
            <ul className="list-group">
                {projects.map((p, idx) => (
                    <li key={idx} className="list-group-item">
                        <Link to={`/project/${idx+1}`}>{p}</Link>
                    </li>
                ))}
            </ul>

            {showModal && (
                <Modal title="New Project" onClose={() => setShowModal(false)}>
                    <input 
                        className="form-control" 
                        placeholder="Project name" 
                        value={newProject} 
                        onChange={e => setNewProject(e.target.value)} 
                    />
                    <button className="btn btn-success mt-2" onClick={handleCreate}>Create</button>
                </Modal>
            )}
        </div>
    );
}

export default Dashboard;
